using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.SceneManagement;public class PauseMenu : MonoBehaviour{    //listing variables needed    public static bool GameIsPaused = false;    public GameObject pauseMenuUI;    void Update()    {        //alternate way to acces the pause menu as we are coding on a laptop.         if (Input.GetKeyDown(KeyCode.Escape))        {            //if the game is paused then you can resume it, else you can pause it.            if (GameIsPaused)            {                Resume();            }            else            {                Pause();            }        }    }    //resume method, sets pause menu and gameIsPaused to false after being resumed so the player can pause again.    public void Resume()    {        pauseMenuUI.SetActive(false);        Time.timeScale = 1F;        GameIsPaused = false;    }    //pause method, if the game is running the pause button calls this upon a click or"esc", it sets pauseMenu and GameIsPaused to true so the player can either resume or go back to main menu.    public void Pause()    {        pauseMenuUI.SetActive(true);        Time.timeScale = 0F;        GameIsPaused = true;    }    //method for returning to main menu, once the game is paused, player can quit game to main menu if they click on the main menu button.    public void LoadMenu()    {        Time.timeScale = 1f;        SceneManager.LoadScene(0);    }   }